import React from "react"
import renderer from "react-test-renderer"
import Index from "../index"
import { StaticQuery } from "gatsby"

beforeEach(() => {
  StaticQuery.mockImplementationOnce(({ render }) =>
    render({
      site: {
        siteMetadata: {
          title: `Jian Sorge`,
          description: `Starter description`,
        },
      },
    })
  )
})

describe("Index", () => {
  it("renders correctly", () => {
    const data = {
      site: {
        siteMetadata: {
          title: `Gatsby contentful starter`,
          description: `Starter description`,
        },
      },
      homeImage: {
        childImageSharp: {
          fluid: {
            base64:
              "data:image/jpeg;base64,",
            aspectRatio: 1.7489421720733427,
            src:
              "/static/939d6e8fd03b3a5f95030c5e1e74de9e/0c160/index-hero.jpg",
            srcSet:
              "/static/939d6e8fd03b3a5f95030c5e1e74de9e/ac6ef/index-hero.jpg 1040w,\n/static/939d6e8fd03b3a5f95030c5e1e74de9e/5d93e/index-hero.jpg 2080w,\n/static/939d6e8fd03b3a5f95030c5e1e74de9e/0c160/index-hero.jpg 2480w",
            sizes: "(max-width: 2480px) 100vw, 2480px",
          },
        },
      },
    }
    const tree = renderer.create(<Index data={data} />).toJSON()
    expect(tree).toMatchSnapshot()
  })
})
